function getAge(e){var t=((new Date).getTime()-e.getTime())/31536e6,n=Math.floor(t),a=12*(t-n),o=Math.floor(a),g=31*(a-o),i="",r="",c="and";return Math.floor(g)>20&&(o+=1),1==n?i="Jahr":0==n?(n="",c=""):i="Jahre",1==o?r="Monat":0==o?(o="",c=""):r="Monate",n+" "+i+" "+c+" "+o+" "+r}function getActive(){document.getElementById("index").classList.add("active")}document.getElementById("age").innerHTML=getAge(new Date("12/28/2020")),getActive();