function getAge(e){var t=((new Date).getTime()-e.getTime())/31536e6,a=Math.floor(t),n=12*(t-a),r=Math.floor(n),o=31*(n-r),g="",l="",i="and";return Math.floor(o)>20&&(r+=1),1==a?g="Jahr":0==a?(a="",i=""):g="Jahre",1==r?l="Monat":0==r?(r="",i=""):l="Monate",a+" "+g+" "+i+" "+r+" "+l}function getActive(){document.getElementById("index").classList.add("active")}function getGallery(){var e=require("fs").readdirSync("/assets/gallery/thumbs");console.log(e)}document.getElementById("age").innerHTML=getAge(new Date("12/28/2020")),getActive(),getGallery();